.details-section {
    width: 100%;
    background: #FAFAFA;
    padding: 80px 160px;
}

.details-container {
    max-width: 1440px;
    margin: 0 auto;
}

.details-content {
    background: #FFFFFF;
    border-radius: 8px;
    padding: 48px 40px;
    display: flex;
    flex-direction: column;
    gap: 32px;
}

.details-title {
    font-family: 'ABeeZee', sans-serif;
    font-weight: 400;
    font-style: italic;
    font-size: 20px;
    line-height: 24px;
    letter-spacing: 0;
    margin: 0;
}

.details-description {
    font-family: 'ABeeZee', sans-serif;
    font-weight: 400;
    font-style: italic;
    font-size: 14px;
    line-height: 24px;
    letter-spacing: 0;
    color: #6C6C6C;
    margin: 0;
}

.characteristics-grid {
    display: flex;
    flex-direction: column;
    gap: 16px;
}

.characteristic-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-bottom: 16px;
    border-bottom: 1px solid #EDEDED;
}

.characteristic-row:last-child {
    border-bottom: none;
}

.characteristic-label {
    font-family: 'Abel', sans-serif;
    font-weight: 400;
    font-style: normal;
    font-size: 16px;
    line-height: 24px;
    letter-spacing: 0;
    color: #6C6C6C;
}

.characteristic-value {
    font-family: 'Abel', sans-serif;
    font-weight: 400;
    font-style: normal;
    font-size: 16px;
    line-height: 24px;
    letter-spacing: 0;
    color: #000000;
    text-align: right;
}

/* Адаптивность для мобильных */
@media (max-width: 768px) {
    .details-section {
        padding: 40px 20px;
    }
    
    .details-content {
        padding: 24px 20px;
    }
}

@media (max-width: 1200px) {
    .details-section {
        padding: 60px 80px;
    }
}

